interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"05bbcbf0-44e0-11ed-84ee-0521f1bbf56e","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"b4e5c64d-ee1b-400b-ad8a-a1b8ffc2f66b","nodes":[{"object":"text","text":"Key Assumptions","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"9577d0da-46e8-4ef8-a257-162de01dbbaf"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"03cd9318-4551-4099-b03f-6e7f04e1b495","nodes":[{"object":"text","text":"While it is common practice to use the WACC as the discount rate in capital budgeting, it is important to be aware of the underlying assumptions. We examine the critical assumptions here and then explore these assumptions further in the context of an application.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"5d29a324-c253-4c5a-9419-4f2aad7c45d3"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"b5995600-e1b1-41bc-90b5-add51fbf206f","nodes":[{"object":"text","text":"Assumption 1: Average Risk","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"35107205-daf9-4ec0-8c8f-d07601449923"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"96230bba-03f5-4710-b4ca-b395d2d083de","nodes":[{"object":"text","text":"We assume initially that the market risk of the project is equivalent to the average market risk of the firm’s investments. In that case, we assess the project’s cost of capital based on the risk of the firm.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"4232065a-d08b-47ef-9abc-58c031e31808"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"744e9da4-3190-47fc-85b9-2279440fede6","nodes":[{"object":"text","text":"Assumption 2: Constant Debt-Equity Ratio","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"d7399a39-e9d7-430b-b46a-f89ed4fbd174"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"bef5c608-44a5-4e34-9a02-f15934416a80","nodes":[{"object":"text","text":"We assume that the firm adjusts its leverage continuously to maintain a constant ratio of the market value of debt to the market value of equity—a relationship referred to as the ","marks":[]},{"data":{"ref":"b2a80d18-cf73-4d9a-bf9f-d8faeacfd099","stage":{"ref":"32831d7c-3ace-4ebf-8f04-409b0d7c01c1","sections":[{"ref":"5a870d2a-e8e1-48a5-a0dd-289f25657612","rows":[{"ref":"d5966e83-b58a-4813-b0b8-be080c1c6426","columns":[{"ref":"125b3490-dd3f-40ae-b550-46d22dd4c673","stageElements":[{"ref":"39fe658d-0edd-43e7-b242-bde07b58e515","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"c3012f5d-8ca8-421d-b69a-42f77f0c52a3","nodes":[{"object":"block","type":"paragraph","ref":"a0b54c4f-1dda-489d-aa68-041415b2db82","nodes":[{"object":"text","text":"A ratio of the market value of debt to the market value of equity.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"debt-equity ratio","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"cc61ad77-e52b-4944-8b7f-b9d8ff70d752"},{"object":"text","text":". This policy determines the amount of debt the firm will take on when it accepts a new project. It also implies that the risk of the firm’s equity and debt, and therefore its WACC, will not fluctuate owing to leverage changes.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"58168f93-9400-4459-a9db-8ad3ec564091"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"2cef1deb-6581-414b-8bdc-090ad8b3fa79","nodes":[{"object":"text","text":"Assumption 3: Limited Leverage Effects","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"ba68a1a2-b54e-4b8f-97db-36d4bcb4be2d"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"1885ac75-6cc2-4dbc-b06c-781b1ed3c582","nodes":[{"object":"text","text":"We assume initially that the main effect of leverage on valuation follows from the interest tax deduction. We assume that any other factors (such as possible financial distress) are not significant at the level of debt chosen. We discuss these other factors in detail in ","marks":[]},{"object":"inline","type":"reference-link","ref":"867f8e1b-6e76-4f14-9f52-bf7548ff650c","nodes":[{"object":"text","text":"Chapter 16","marks":[]}],"data":{"reference":"637adc80-44e1-11ed-8442-dbcbc27803ca"}},{"object":"text","text":".","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"1883edd0-8ab0-4456-8a79-8ff1fda571b9"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"8c64dcd7-7794-4ed5-a992-fbf206b9f4ed","nodes":[{"object":"text","text":"Assumptions in Practice","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"85e6abc3-5639-4295-a50a-33ac0e42dc2d"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"6ced105c-db3c-4f13-bb6e-00cdd8351934","nodes":[{"object":"text","text":"These assumptions are reasonable for many projects and firms. The first assumption is likely to fit typical projects of firms with investments concentrated in a single industry. In that case, the market risk of both the project and the firm will primarily depend on the sensitivity of the industry to the overall economy. The second assumption, while unlikely to hold exactly, reflects the fact that firms tend to increase their levels of debt as they grow larger; some may even have an explicit target for their debt-equity ratio.","marks":[]},{"data":{"ref":"9b0f278e-5368-404c-ab25-177ad64aac19","stage":{"ref":"484258fb-22a3-4f35-94db-6bec3ce83a9b","sections":[{"ref":"8918a08d-e003-4e77-b5d3-6b1df7b87c54","rows":[{"ref":"78d5a9be-0592-4118-b749-74b639b8eae4","columns":[{"ref":"3b5c241a-a06e-4123-879c-24c56f4a6d50","stageElements":[{"ref":"88f1a302-f061-46da-8855-e69dc1362cc9","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"71ec75fd-1133-4505-8d46-3cabae66ebbf","nodes":[{"object":"block","type":"paragraph","ref":"564b6000-16cc-4b50-b496-87a0a76b8d13","nodes":[{"object":"text","text":"We discuss the tradeoff between debt and equity and the concept of a target debt-equity ratio in ","marks":[]},{"object":"inline","type":"reference-link","ref":"6bf5546f-f58e-4098-9a20-9fccc81677ac","nodes":[{"object":"text","text":"Chapter 16","marks":[]}],"data":{"reference":"637adc80-44e1-11ed-8442-dbcbc27803ca"}},{"object":"text","text":".","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"5","marks":[]}],"object":"inline","type":"footnote","ref":"075d196f-f0ed-4b3d-8b42-ea573d4b7ffd"},{"object":"text","text":" Finally, for firms without very high levels of debt, the interest tax deduction is likely to be the most important capital structure effect for the capital budgeting decision. Hence, the third assumption is a reasonable starting point to begin our analysis.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"aa4ed52f-ed6d-48d2-82e5-9ff1e3e34480"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"0688216f-53d2-4551-b2cf-035eef413077","nodes":[{"object":"text","text":"Of course, while these three assumptions may be a reasonable approximation in many situations, there are certainly projects and firms for which they do not apply. In the following section, we apply the WACC method under all three assumptions. Next, we relax the first assumption, which states that the project has average risk. (We will relax the other two assumptions in later chapters.)","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"bce4bf3b-d5eb-4230-a6eb-1eccb22c90d4"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Key Assumptions"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}