interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"06ca07a0-44e0-11ed-8a24-6227a9bca7ec","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-one","ref":"fbc3cbd2-d6bc-4489-a9c8-1930ce86e80e","nodes":[{"object":"text","text":"14.3 IPO Puzzles","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"150e5d98-eb5c-4804-aaba-dfa38cf77e3c"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"208ccc8b-e9ab-45cf-8d82-b2d73bca1698","nodes":[{"object":"text","text":"Four characteristics of IPOs puzzle financial economists, and all are relevant to the financial manager:","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"b15cf1a6-2f9f-44c2-bbd5-4bf57e7ea83f"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"ordered-list","data":{"listOptionType":"1","startValue":1},"ref":"609956fa-d82e-416c-b624-1d8162d6a299","nodes":[{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"50ae7509-fa76-481e-9f8d-cb8f4ce163e5","nodes":[{"object":"block","type":"paragraph","ref":"0a67b1e3-43a9-44ee-ae80-5bc16c1f1e4f","nodes":[{"object":"text","text":"On average, IPOs appear to be underpriced: The price at the end of trading on the first day is often substantially higher than the IPO price.","marks":[]}],"data":{}}]},{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"560918d6-92e1-4806-a82b-548751481524","nodes":[{"object":"block","type":"paragraph","ref":"e296cc9b-5ee8-44ae-ac18-787c9ddba848","nodes":[{"object":"text","text":"The number of IPOs is highly cyclical. When times are good, the market is flooded with IPOs; when times are bad, the number of IPOs dries up.","marks":[]}],"data":{}}]},{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"3640b370-e0de-421c-b437-b63c11c08d71","nodes":[{"object":"block","type":"paragraph","ref":"503ffdf5-e9b5-4147-b7f5-a84d33de2cb8","nodes":[{"object":"text","text":"The transaction costs of the IPO are very high, and it is unclear why firms willingly incur such high costs.","marks":[]}],"data":{}}]},{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"9c13d980-56e4-4f23-ae40-d1a69feacd0f","nodes":[{"object":"block","type":"paragraph","ref":"03b9b761-7943-4ea2-9f0b-a11569b93fa5","nodes":[{"object":"text","text":"The long-run performance of a newly public company (three to five years from the date of issue) is poor. That is, on average, a three- to five-year buy-and-hold strategy appears to be a bad investment.","marks":[]}],"data":{}}]}]}]}},"config":{"cssClass":""},"ref":"43041c4e-9c84-48da-a7ec-648c317837d2"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"1c8c1e57-2429-4849-bb1a-3ae3a93fc96b","nodes":[{"object":"text","text":"We will now examine each of these puzzles that financial economists seek to understand.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"1ffa0282-e702-4741-b2bb-4eac4a1b15e6"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"14.3 IPO Puzzles"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}