interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"07522360-44e0-11ed-8469-f0513334cc91","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"f6a470f8-9d0a-4b9c-8577-e480cb314370","nodes":[{"object":"text","text":"Private Debt","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"a6928d91-4b4f-4ce1-bd1a-40a775693fec"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"39adf61e-bd91-4a0a-9b44-f4c7b5b9a545","nodes":[{"object":"text","text":"The first debt financing many young firms undertake is a bank loan. However, even very large, established firms use bank loans as part of their debt financing. Bank loans are an example of ","marks":[]},{"data":{"ref":"4ae7c776-3c22-4aa9-b6b8-c7c8fa49a746","stage":{"ref":"f3944e3b-5459-4828-8e62-c6b7445a9ec7","sections":[{"ref":"8595e8be-5a76-44be-be45-e251f4237f66","rows":[{"ref":"139da961-6019-4f8c-8510-501a4d8d920a","columns":[{"ref":"8a3d1e5d-4b7f-4456-9db7-eeb039fc82ac","stageElements":[{"ref":"6e894959-da09-4190-adc7-2a2c4920e56e","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"c3608b9f-9829-48b3-8114-4895902331b2","nodes":[{"object":"block","type":"paragraph","ref":"76304343-6033-4eb4-a249-e1b2c01f662c","nodes":[{"object":"text","text":"Debt that is not publicly traded.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"private debt","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"ffc820c2-8e4d-49c3-a9f6-d161a897bc31"},{"object":"text","text":", debt that is not publicly traded. The private debt market is larger than the public debt market. Private debt has the advantage that it avoids the cost and delay of registration with the U.S. Securities and Exchange Commission (SEC). The disadvantage is that because it is not publicly traded, it is illiquid, meaning that it is hard for a holder of the firm’s private debt to sell it in a timely manner.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"8418f3e8-b0ce-4c34-a606-8b16a1ef6670"}]}]}]},{"cssClass":"aside-group  Sidebar_1Aside","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"759a3e7b-6653-4046-b5f0-e66d93a633bd","nodes":[{"object":"text","text":"Debt Financing at Hertz: Bank Loans","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"b5830b2c-7888-430b-be4f-4d9049048a87"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"2ea2fe73-82b8-4863-a913-869d9d844a2e","nodes":[{"object":"text","text":"As part of the transaction with CDR, Hertz took out more than $2 billion in bank loans. Hertz negotiated a $1.7 billion syndicated term loan with a seven-year term. Deutsche Bank AG negotiated the loan and then sold portions of it off to other banks—mostly smaller regional banks that had excess cash but lacked the resources to negotiate a loan of this magnitude by themselves. In addition to the term loan, Hertz negotiated an asset-backed revolving line of credit (for five years and $1.6 billion), which it could use as needed. Hertz’s initial draw on the line of credit was $400 million.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"99fe6bde-1047-4775-a5e2-5086d3ed159a"}]}]}]},{"rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"86a900c4-8a41-4101-972b-c6cc6882f1cb","nodes":[{"object":"text","text":"There are several segments of the private debt market: ","marks":[]},{"object":"text","text":"bank loans","marks":[{"type":"italic"}]},{"object":"text","text":" (","marks":[]},{"object":"text","text":"term loans","marks":[{"type":"italic"}]},{"object":"text","text":" and ","marks":[]},{"object":"text","text":"lines of credit","marks":[{"type":"italic"}]},{"object":"text","text":") and ","marks":[]},{"object":"text","text":"private placements","marks":[{"type":"italic"}]},{"object":"text","text":".","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"146a74ff-4483-440f-90a6-505f42af38ed"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"8e41a05b-9e60-4a02-9474-683d61d19d91","nodes":[{"object":"text","text":"Bank Loans","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"f6b77dec-9c29-43f5-9d5a-1decc84f7f91"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"240d9810-13dd-4ea4-a77a-e0eac7b3b8b8","nodes":[{"object":"text","text":"A ","marks":[]},{"data":{"ref":"ebaba3e2-ce14-4d58-bb7a-622d9004d0f4","stage":{"ref":"a27f1681-c09b-4286-a2ff-91351370270e","sections":[{"ref":"46fba6e5-b005-4fbc-8ab0-c285c6dbd7f6","rows":[{"ref":"a31c7371-2000-4594-8fdf-382518941aa0","columns":[{"ref":"b94365ce-3199-4fa3-ba95-cfb4c861c4d9","stageElements":[{"ref":"bfa66b72-f436-47dc-99dd-6ae2ad9e6082","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"f5635f63-7606-4d32-80d1-b444947b14aa","nodes":[{"object":"block","type":"paragraph","ref":"c65953bb-698e-489d-86be-1faa2202ca08","nodes":[{"object":"text","text":"A bank loan that lasts for a specific term.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"term loan","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"692e6251-4704-45d7-bc1e-699921799416"},{"object":"text","text":" is a bank loan that lasts for a specific term. When a single loan is funded by a group of banks rather than a single bank, it is called a ","marks":[]},{"data":{"ref":"cbd4f772-6159-4baf-83c9-9ffc25851deb","stage":{"ref":"5e5d662d-482c-4575-ad1c-b7416e9a603c","sections":[{"ref":"a877209c-5781-4cc7-9ac9-9670310ef568","rows":[{"ref":"96025dfc-aecf-42c6-882e-aa28a1d8d431","columns":[{"ref":"f77fa249-3ee3-4c23-8d3d-cdfe7575c741","stageElements":[{"ref":"9e3d058d-9273-4936-bf6c-2b11f669b105","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"24d15cd5-c9e1-41ba-961c-e8a36ae30232","nodes":[{"object":"block","type":"paragraph","ref":"06fc7f49-771d-4ac5-851d-4784f1cb2160","nodes":[{"object":"text","text":"A single loan that is funded by a group of banks rather than a single bank.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"syndicated bank loan","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"3628463f-2133-478d-aed7-d0ea883ac07c"},{"object":"text","text":". Usually, one member of the syndicate (the lead bank) negotiates the terms of the bank loan. Many companies establish a ","marks":[]},{"data":{"ref":"797ce0d8-7a9c-4e17-988a-0fbd8df7f71d","stage":{"ref":"742787ca-1e19-46d0-99fc-11fe998b5931","sections":[{"ref":"712138d6-e88c-4988-bdcd-4ecf252973a3","rows":[{"ref":"b5078345-70aa-49d9-a22a-913a1c02b879","columns":[{"ref":"c420513c-26c2-4810-b0e9-9dc4a1c90e28","stageElements":[{"ref":"1a7b49c0-e55f-47d2-9b23-f80b4c89a261","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"6d3d0834-4594-42b2-93e6-1301da57c29b","nodes":[{"object":"block","type":"paragraph","ref":"87b48df6-05f6-47d1-8a2a-46c103d2faf0","nodes":[{"object":"text","text":"A credit commitment for a specific time period, typically two to three years, which a company can use as needed.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"revolving line of credit","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"9c10566c-4e13-4113-ad93-7db47265d272"},{"object":"text","text":", a credit commitment for a specific time period up to some limit, typically two to three years, which a company can use as needed. A company may be able to get a larger line of credit or a lower interest rate if it secures the line of credit by pledging an asset as collateral. Such a line of credit is referred to as an ","marks":[]},{"data":{"ref":"3521ee38-5603-4329-a5d8-f19b410fe494","stage":{"ref":"4a2ec46c-8af7-45c0-bf92-abb75dde9199","sections":[{"ref":"b2141c56-3988-4185-93d9-26a4cb8e88f6","rows":[{"ref":"fa6d45aa-7415-4119-8e57-c18882a78aad","columns":[{"ref":"1d6ad628-b6f5-4bb3-a9e8-02cd63ac7fde","stageElements":[{"ref":"370f468b-2b46-44fd-a86c-c84b1f84b087","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"fa3a2213-4225-468b-bb33-42d197ed0bc0","nodes":[{"object":"block","type":"paragraph","ref":"00e9cf07-ddec-4600-893f-7d0d85d2951e","nodes":[{"object":"text","text":" A type of credit commitment, in which the borrower secures a line of credit by pledging an asset as collateral. ","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"asset-backed line of credit","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"f5d947d8-4d84-49b0-b48d-b7d050e959be"},{"object":"text","text":".","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"495f9780-5e71-429f-852b-56989e9455b5"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"d01a0605-a36e-4b93-8cc6-9b8a01e2d18b","nodes":[{"object":"text","text":"Private Placements","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"ae01632c-5d6a-458e-9f66-c3a9c873e0e0"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"17394182-d5ec-48b6-ab56-35997c2f1c68","nodes":[{"object":"text","text":"Recall from ","marks":[]},{"object":"inline","type":"reference-link","ref":"e97a20be-b8f4-495a-9e0f-b2d632987481","nodes":[{"object":"text","text":"Chapter 6","marks":[]}],"data":{"reference":"f6ca0070-44e0-11ed-8442-dbcbc27803ca"}},{"object":"text","text":" that corporate bonds are securities issued by corporations. They account for a significant amount of invested capital. At the beginning of 2019, the value of outstanding U.S. corporate bonds was above $9.2 trillion. Bonds can be issued publicly or placed privately. A ","marks":[]},{"data":{"ref":"81125496-2cde-498b-b703-3ddaa113d35c","stage":{"ref":"f0e1b30b-4166-485d-b35e-0c7bec329bd8","sections":[{"ref":"3392d0b5-bec8-40b6-b2c9-4aa4f719a344","rows":[{"ref":"c4495d99-b354-44f5-b1a0-b5a4059e8ebe","columns":[{"ref":"d5253c83-f1bb-4237-9d18-8971a36153bc","stageElements":[{"ref":"d6107ea2-e1a7-42cd-8497-a0784951e594","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"e76fc384-d391-4943-90bf-a02e108786fb","nodes":[{"object":"block","type":"paragraph","ref":"3a201ec8-8d7d-4df6-b5ca-6b9dac57b9ee","nodes":[{"object":"text","text":"A bond issue that does not trade on a public market but rather is sold to a small group of investors.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"private placement","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"97191750-0654-42c5-b183-14434ca54cc7"},{"object":"text","text":" is a bond issue that does not trade on a public market but rather is sold to a small group of investors. Because a private placement does not need to be registered with the SEC, it is less costly to issue and often a simple promissory note is sufficient. Privately placed debt also need not conform to the same standards as public debt; as a consequence, it can be tailored to the particular situation.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"564cbf96-15ac-46cd-98d6-12350cfa5aab"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"67bcd724-8075-4d4b-9fd7-8069860cf3b3","nodes":[{"object":"text","text":"In 1990, the SEC issued Rule 144A, which significantly increased the liquidity of certain privately placed debt. Private debt issued under this rule can be traded by large financial institutions among themselves. The rule was motivated by a desire to increase the access of foreign corporations to U.S. debt markets. Bonds that are issued under this rule are nominally private debt, but because they are tradable between financial institutions they are only slightly less liquid than public debt. Many firms issue debt under Rule 144A with the explicit promise to publicly register the debt within a certain time frame. The advantage of this approach to debt financing is that companies can raise the capital quickly and then spend the time it takes to comply with all of the filing requirements.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"895e52f8-5070-4b5f-927e-6d9fa39abea7"}]}]}]},{"cssClass":"aside-group  Sidebar_1Aside","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"1d9ae962-de53-45ad-8a7f-d2cd4ec77571","nodes":[{"object":"text","text":"Debt Financing at Hertz: Private Placements","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"48d9dd5f-01b6-42bd-b840-5b894b60f5d1"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"d4a271c7-f645-4fa8-b1c9-d2b29f24c0b0","nodes":[{"object":"text","text":"Hertz privately placed an additional $4.2 billion of U.S. asset-backed securities and $2.1 billion of international asset-backed securities. In this case, the assets backing the debt were the fleet of rental cars Hertz owned; hence, this debt was termed “fleet debt.”","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"4bd39c81-0ac6-4972-8114-2b338e9a90ee"},{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"58caff25-5f8d-4ee1-99e6-930430d6ba0b","nodes":[{"object":"text","text":"Hertz had an additional $2.7 billion bond issue that it issued under Rule 144A. As part of the offering, it agreed to publicly register the bonds within 390 days.","marks":[]},{"data":{"ref":"3d5085b5-70d1-4c7a-9f98-a9ded77cec1f","stage":{"ref":"f36c865d-1f1e-4be8-a7ed-1a6cb13ef936","sections":[{"ref":"3fc516c4-f3ca-4b54-ac68-1dbe8831a781","rows":[{"ref":"047458db-11f4-4f76-b1c9-950de9fdedc9","columns":[{"ref":"42ade9f8-fe9c-49c7-8200-87404c545125","stageElements":[{"ref":"5ff4ac93-3dd1-4e3e-adaf-bc21895da2c5","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"bfbfe7e3-c871-412a-afe9-a9c29a1cee10","nodes":[{"object":"block","type":"paragraph","ref":"88db250b-97fb-493e-b19a-f3bd7271415a","nodes":[{"object":"text","text":"If Hertz failed to fulfill this commitment, the interest rate on all the outstanding bonds would increase by 0.5%.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"*","marks":[]}],"object":"inline","type":"footnote","ref":"dd22025a-e6b0-4b42-b81f-e6692a081323"},{"object":"text","text":" Because the debt was marketed and sold with the understanding that it would become public debt, we classified that issue as public debt.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"8400a8ff-ff49-44c7-a399-0f341a111b40"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Private Debt"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}