interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"0d08ff90-44e0-11ed-8bd2-e3db3f26e389","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-one","ref":"3921bb80-8bec-4020-a9ae-7905aef4d2fc","nodes":[{"object":"text","text":"19.5 Inventory Management","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"7927cf46-d21e-4d55-9fc8-c445b385010f"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"0b75bc7d-9d18-4655-8e53-bacc4efdbb09","nodes":[{"object":"text","text":"As we discussed, in a perfect capital markets setting, firms would not need to have accounts payable or receivable. Interest rates on trade credit would be competitive, and firms could use alternative sources of financing. However, unlike trade credit, inventory represents one of the required factors of production. Therefore, even in a perfect markets setting, firms still need inventory.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"1fbca214-8eac-4bd5-8e5d-160cd436d57d"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"9f9cc5cb-ed2b-4423-a6b4-29024b6750bb","nodes":[{"object":"text","text":"Inventory management receives extensive coverage in a course on operations management. Nevertheless, it is the firm’s financial manager who must arrange for the financing necessary to support the firm’s inventory policy and who is responsible for ensuring the firm’s overall profitability. Therefore, the role of the inventory manager is to balance the costs and benefits associated with inventory. Because excessive inventory uses cash, efficient management of inventory increases firm value.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"b3d66d22-e727-4416-8e1f-9ab789cd749a"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"19.5 Inventory Management"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}