interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"0d0fdd60-44e0-11ed-867d-2438cbae5ca9","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"b71ddbf8-97dd-4280-b639-66a2e57f7b25","nodes":[{"object":"text","text":"Motivation for Holding Cash","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"fedd1804-dc6f-4e68-8d0e-1cf5375a1966"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"91f7565f-946c-4418-a5d2-21fc471b923a","nodes":[{"object":"text","text":"There are three reasons why a firm holds cash:","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"e1943209-5ff5-416d-8530-88ee0fabb7df"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"unordered-list","data":{"listOptionType":"","startValue":""},"ref":"162e44ae-2104-41bf-8f80-6673db5efdfd","nodes":[{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"75f14997-016d-430c-9b9c-67d6a8b4e0c2","nodes":[{"object":"block","type":"paragraph","ref":"ef66483f-5f3f-4948-96e2-3b7328c8616e","nodes":[{"object":"text","text":"To meet its day-to-day needs","marks":[]}],"data":{}}]},{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"15850f6a-bc25-4c43-aef8-e5f9c9f4f3ab","nodes":[{"object":"block","type":"paragraph","ref":"bcb8bdb9-a69f-4683-95df-0da7ea9be9f3","nodes":[{"object":"text","text":"To compensate for the uncertainty associated with its cash flows","marks":[]}],"data":{}}]},{"object":"block","type":"list-item","data":{"direction":"auto"},"ref":"e8011012-d700-4f3a-973a-1bf9c66b822a","nodes":[{"object":"block","type":"paragraph","ref":"ab456ea0-ea44-45af-a827-136c399e55b5","nodes":[{"object":"text","text":"To satisfy bank requirements","marks":[]}],"data":{}}]}]}]}},"config":{"cssClass":""},"ref":"8236d1cd-1d23-41d3-86f2-c8dd9f8ce191"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"9294101c-1b62-43fc-919f-fa99155a825f","nodes":[{"object":"text","text":"We will now examine each of these motivations for holding cash in detail.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"a4f294bd-e162-4b0a-a5b6-3ab0af46b050"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"e3a168c8-9d21-4f6b-b6b8-8506cb3aaf98","nodes":[{"object":"text","text":"Transactions Balance","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"21a654d8-6909-459f-b5f2-2d542dd35bd3"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"ab64bb7c-24dc-4253-99c4-c5b8b5b3a98c","nodes":[{"object":"text","text":"Just like you, a firm must hold enough cash to pay its bills. The amount of cash a firm needs to be able to pay its bills is sometimes referred to as a ","marks":[]},{"data":{"ref":"3b1c8605-adc8-42f4-a1aa-0fb5fe51bd2b","stage":{"ref":"9b53a8fa-0f7e-4261-8d35-c1cff91983da","sections":[{"ref":"349d638e-7cf0-4d59-b143-0f4ed995f299","rows":[{"ref":"341f2624-c257-4a4d-9a03-d4bc7155aac9","columns":[{"ref":"b294ca10-9f69-463f-8b1e-4560f22a1031","stageElements":[{"ref":"2c2eefe2-0ae1-458f-89f9-6946c5b07b26","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"2484c8f3-5583-4497-ae31-a0e31078ee77","nodes":[{"object":"block","type":"paragraph","ref":"9d68e8d4-4f29-4270-b028-ab48de6ab761","nodes":[{"object":"text","text":"The amount of cash a firm needs to pay its bills.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"transactions balance","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"7d3cc0ad-bc40-4fcd-8a66-7373a8b48787"},{"object":"text","text":". The amount of cash a firm needs to satisfy the transactions balance requirement depends on both the average size of the transactions made by the firm and the firm’s cash cycle, discussed earlier in the chapter.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"23f99f95-af7d-460a-961a-ba869a982fd7"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"c0c6e3e8-dc40-4066-b5ba-0cd0c71a4873","nodes":[{"object":"text","text":"Precautionary Balance","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"d79d40a4-e82e-4073-b72c-8ed52f2957b6"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"fbded79d-ce06-4fd1-8057-c5cfafe0071b","nodes":[{"object":"text","text":"The amount of cash a firm holds to counter the uncertainty surrounding its future cash needs is known as a ","marks":[]},{"data":{"ref":"18d27636-f093-4529-84a5-ebf9a2ab9a99","stage":{"ref":"070ac037-c056-4714-b33d-c8bf9be33056","sections":[{"ref":"4d64a876-b412-4b83-a212-9c3d8df6f2eb","rows":[{"ref":"25a4ffae-9e08-49aa-b342-9fa9f9d75915","columns":[{"ref":"fee45403-05a0-494a-8ddf-0eb45710577f","stageElements":[{"ref":"c6b72ab4-2b7d-4442-b189-0d6bcaa9e350","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"b8767079-4e9f-4e79-8be5-77b17a74dbb7","nodes":[{"object":"block","type":"paragraph","ref":"413a9e57-85c8-4771-935c-8cb37358cc58","nodes":[{"object":"text","text":"The amount of cash a firm holds to counter the uncertainty surrounding its future cash needs.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"precautionary balance","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"afc91b43-9495-49ce-8c8f-74b8efeb861a"},{"object":"text","text":". The size of this balance depends on the degree of uncertainty surrounding a firm’s cash flows. The more uncertain future cash flows are, the harder it is for a firm to predict its transactions need, so the larger the precautionary balance must be.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"9f137821-351a-4de7-980f-02e463067d53"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"c06c7fb3-fc2e-42e1-884d-6610ad4a4d8b","nodes":[{"object":"text","text":"Compensating Balance","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"be68307a-6843-4f26-8ea0-de8b4f723755"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"42a5b642-325b-4e4b-affe-e5092b5b838e","nodes":[{"object":"text","text":"A firm’s bank may require it to hold a ","marks":[]},{"data":{"ref":"45a0bd2c-0a7f-41b9-a4eb-744cb9d282a0","stage":{"ref":"ea1cefc4-c558-4b21-9791-91596479955c","sections":[{"ref":"61d183ea-24dc-4c31-a66c-3e4f95619ab0","rows":[{"ref":"dbbb4b9e-c6a2-4f06-81d2-b39274328dd3","columns":[{"ref":"240c9709-fa7f-4a4f-a05a-e858041b96cb","stageElements":[{"ref":"1d496347-7e9d-4f85-9c0f-23afa09b6a77","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"e04250ef-e47c-4d2e-8d78-2e2e4dfd75c2","nodes":[{"object":"block","type":"paragraph","ref":"f7cd6340-fe41-42b9-9a4b-c051acfef968","nodes":[{"object":"text","text":"An amount a firm’s bank may require the firm to maintain in an account at the bank as compensation for services the bank performs.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"compensating balance","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"5f1c68e7-634a-47ea-9ca9-833d3ae24543"},{"object":"text","text":" in an account at the bank as compensation for services that the bank performs. Compensating balances are typically deposited in accounts that earn either little or no interest. This arrangement is similar to a bank offering individuals free checking as long as their balances do not fall below a certain level—say, $1000. Essentially, the customer has $1000 cash that she cannot use unless she is willing to pay a service charge. Similarly, the cash that a firm has tied up to meet a compensating balance requirement is unavailable for other uses.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"dfe2aad0-52f4-44ef-94bb-6615930d45e7"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Motivation for Holding Cash"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}