interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"0d850d60-44e0-11ed-8e80-1638dccb63e0","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-one","ref":"231ffe1b-cbc4-47c2-9152-fbe6accdebab","nodes":[{"object":"text","text":"20.2 The Matching Principle","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"d8390a08-2bc6-4362-8b03-69d93886db86"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"18208d82-8d50-4f44-94e5-c819e871e5f6","nodes":[{"object":"text","text":"In a perfect capital market, the choice of financing is irrelevant; thus, how the firm chooses to finance its short-term cash needs cannot affect the firm’s value. In reality, important market frictions exist, including transaction costs. For example, one transaction cost is the opportunity cost of holding cash in accounts that pay little or no interest. Firms also face high transaction costs if they need to negotiate a loan on short notice to cover a cash shortfall. Firms can increase their value by adopting a policy that minimizes these kinds of costs. One such policy is known as the ","marks":[]},{"object":"text","text":"matching principle","marks":[{"type":"italic"}]},{"object":"text","text":". The ","marks":[]},{"data":{"ref":"dc452836-cfe4-427d-a428-f81001e63b2f","stage":{"ref":"d35e6745-8a40-46e2-9204-9667b32cfe74","sections":[{"ref":"2ddcef09-60ad-407f-ae53-8f1a14a8cee2","rows":[{"ref":"aa0757e6-ef47-434c-8cc4-e484eb2fb684","columns":[{"ref":"2143d3e5-9b74-408e-9aad-825c96b81a47","stageElements":[{"ref":"3fec51bc-c92a-4579-8a5b-760a44e59dd9","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"56ad57b3-3184-40df-a5e5-b43f9d14b495","nodes":[{"object":"block","type":"paragraph","ref":"10df74ca-812f-408f-9b6b-30279dc9630a","nodes":[{"object":"text","text":"States that a firm’s short-term cash needs should be financed with short-term debt and long-term cash needs should be financed with long-term sources of funds.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"matching principle","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"f348d7b4-8b4a-441c-a5af-d68e4f3b7d47"},{"object":"text","text":" states that short-term cash needs should be financed with short-term debt and long-term cash needs should be financed with long-term sources of funds.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"b375d099-3fe6-4bfa-9f7a-3b87dddc9dde"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"20.2 The Matching Principle"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}