interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"1141ee50-44e0-11ed-8812-bf9b64d1ddd3","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"c45a6cd3-3656-4753-a68e-fee6a307615c","nodes":[{"object":"text","text":"Poison Pills","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"6e0e2249-6254-4c43-8b00-d85af2223781"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"3d88610a-ef4b-444b-8c9e-d98cb5ab47e9","nodes":[{"object":"text","text":"A ","marks":[]},{"data":{"ref":"a0b2d45c-7b6d-44a7-95cf-37e021c4506d","stage":{"ref":"634953c3-d031-43bd-8b2e-a953ddb9e25d","sections":[{"ref":"a68aa3c0-7838-4d3f-802a-5b1d50a0d27d","rows":[{"ref":"2ce37a7e-8e0d-4b59-92ae-9fc470e66c47","columns":[{"ref":"dbfeea79-149c-46a1-8bee-e7c7372a5e35","stageElements":[{"ref":"98042e9f-ff3c-45a3-94f4-d5d42e2921ba","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"886498f3-7b41-4451-9661-016d00392a3f","nodes":[{"object":"block","type":"paragraph","ref":"16867c3a-f277-4b23-85d7-21ff244f2644","nodes":[{"object":"text","text":"A defense against a hostile takeover. It is a rights offering that gives the target shareholders the right to buy shares in either the target or an acquirer at a deeply discounted price.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"poison pill","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"a82a2771-2584-4171-9876-139429cd9bbb"},{"object":"text","text":" is a rights offering that gives existing target shareholders the right to buy shares in the target at a deeply discounted price once certain conditions are met. The acquirer is specifically excluded from this right. Because target shareholders can purchase shares at less than the market price, the rights offering dilutes the value of any shares held by the acquirer. This dilution makes the takeover so expensive for the acquiring shareholders that they choose to pass on the deal.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"31ec08d1-424f-48ee-8fb4-c09cbb28cc25"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"34767915-452d-4465-9f8b-07b5e2c3c526","nodes":[{"object":"text","text":"The poison pill was invented in 1982 by a takeover lawyer, Martin Lipton, who successfully warded off a takeover attempt of El Paso Electric by General American Oil.","marks":[]},{"data":{"ref":"335aa816-2616-4922-8494-375bd9da9a7d","stage":{"ref":"def55ba4-7426-4501-b6db-5e972ffd398a","sections":[{"ref":"4ec8b5d0-ea7e-46f8-b7ef-91806f44c25c","rows":[{"ref":"02ab93ff-d081-4ccc-811e-27d2d4718f03","columns":[{"ref":"78a9619a-5c75-4228-9d44-4ca86508239b","stageElements":[{"ref":"f404cd62-c55b-44ce-8d7e-c953333ef3f5","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"74c0220c-1bf1-4cf4-9f81-5b05d5efc430","nodes":[{"object":"block","type":"paragraph","ref":"b7812c8e-c34c-43ad-b8cd-fcca4906c7a0","nodes":[{"object":"text","text":"For a brief history, see Len Costa, “The Perfect Pill,” ","marks":[]},{"object":"text","text":"Legal Affairs","marks":[{"type":"italic"}]},{"object":"text","text":" (March 2005), ","marks":[]},{"object":"inline","type":"link","ref":"96f47179-a4f2-4808-95f0-b55ffd34709f","nodes":[{"object":"text","text":"www.legalaffairs.org","marks":[]}],"data":{"href":"http://www.legalaffairs.org"}},{"object":"text","text":".","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"10","marks":[]}],"object":"inline","type":"footnote","ref":"f4dacc17-7632-4fc2-ad6f-4f5b43e9e7e1"},{"object":"text","text":" Because the original poison pill goes into effect only in the event of a complete takeover (that is, a purchase of 100% of the outstanding shares), one way to circumvent it is to not do a complete takeover. The first time this work-around was used was by Sir James Goldsmith, who took control of Crown Zellerbach by purchasing slightly more than 50% of the outstanding stock. Because he did not purchase the rest, Crown Zellerbach’s poison pill was ineffective.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"01c9015c-de22-45ea-8630-20815c0af041"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"be234157-c40d-4498-b4fa-7132f50b00d0","nodes":[{"object":"text","text":"In response to the takeover of Crown Zellerbach, corporate lawyers have perfected the original poison pill. Most poison pills now specify that if a raider acquires more than a trigger amount (typically 20%) of the target shares (but chooses not to execute a complete takeover by purchasing all outstanding shares), existing shareholders—with the exception of the acquirer—have the right to buy more shares in the target at a discounted price.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"64e0f967-710f-4f59-b54c-200811decd2a"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"e30d88f1-ce55-46d6-a3ab-3b49ac3829f5","nodes":[{"object":"text","text":"The name ","marks":[]},{"object":"text","text":"poison pill","marks":[{"type":"italic"}]},{"object":"text","text":" comes from the world of espionage. Once caught, a spy is supposed to take his own life by swallowing a poison pill rather than give up important secrets. Poison pills are very effective in stopping takeovers, but where is the suicide analogy? The answer is that by adopting a poison pill, a company effectively entrenches its management by making it much more difficult for shareholders to replace bad managers, thereby potentially destroying value. Financial research has verified this effect. A firm’s stock price typically drops when it adopts a poison pill. Furthermore, once adopted, firms with poison pills have below-average financial performance.","marks":[]},{"data":{"ref":"f7c10d31-c0fb-4fbe-9bcb-a976200aa3a0","stage":{"ref":"c3e04c01-aec0-4ef3-ac42-48e63a4317c5","sections":[{"ref":"721f9f72-2878-4ede-81b8-eb43d11f543f","rows":[{"ref":"79c94f35-b393-4be0-bd0f-c22785e285f6","columns":[{"ref":"2b607c85-cb1f-4f28-ae96-aa3042940c6b","stageElements":[{"ref":"5c6c85bd-1086-4018-9930-e94979f3b369","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"9b96297c-2d30-433c-9976-98afed99af64","nodes":[{"object":"block","type":"paragraph","ref":"5cb5d4c2-01a2-41ad-84f5-fc028fd4410c","nodes":[{"object":"text","text":"P. Malatesta and R. Walkling, “Poison Pill Securities: Stockholder Wealth, Profitability and Ownership Structure,” ","marks":[]},{"object":"text","text":"Journal of Financial Economics","marks":[{"type":"italic"}]},{"object":"text","text":" 20 (1) (1988): 347–376; and M. Ryngaert, “The Effects of Poison Pills Securities on Stockholder Wealth,” ","marks":[]},{"object":"text","text":"Journal of Financial Economics","marks":[{"type":"italic"}]},{"object":"text","text":" 20 (1) (1988): 377–417.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"11","marks":[]}],"object":"inline","type":"footnote","ref":"a28e6043-c0ad-4737-addf-0e5a59a8764a"}],"data":{}}]}},"config":{"cssClass":""},"ref":"a5c9fb2e-15d2-4400-9d7c-02271a50ee50"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"97700607-450f-4fc8-8b83-5a412e0bc5b2","nodes":[{"object":"text","text":"Not surprisingly, companies adopting poison pills are harder to take over, and when a takeover occurs, the premium that existing shareholders receive for their stock is higher. Therefore, because a poison pill increases the cost of a takeover, all else being equal, a target company must be in worse shape (there must be a greater opportunity for profit) to justify the expense of waging a takeover battle.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"db36d199-afa9-417c-9fe3-6e0bd3132c94"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"b0c621c9-6086-4131-898b-d1c847dee7e7","nodes":[{"object":"text","text":"Poison pills also increase the bargaining power of the target firm when negotiating with the acquirer because they make it difficult to complete the takeover without the cooperation of the target board. If used effectively, this bargaining power can allow target shareholders to capture more of the takeover gains by negotiating a higher premium than they would get if no pill existed. Numerous studies on the impact of antitakeover provisions on takeovers have found that such provisions result in higher premiums accruing to existing shareholders of the target company.","marks":[]},{"data":{"ref":"79781015-79dc-44bf-aa04-2d991a3c9a8d","stage":{"ref":"0efe0d04-c1db-420a-9a5e-2db6b0ef9ef3","sections":[{"ref":"375a392c-1747-4fdd-a267-6f2edd6f06d6","rows":[{"ref":"3a624b01-3c9f-4d29-8828-28b4fc2c9e40","columns":[{"ref":"3bcdcb09-3468-46a3-bc87-1b305bd6baf8","stageElements":[{"ref":"07d0e49c-24b1-4e03-b8ac-10211369b489","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"cccd17fe-ff7f-4b96-99ce-b31ac1bce3b0","nodes":[{"object":"block","type":"paragraph","ref":"8fd424d0-a93a-423c-88e8-ac23dcb26591","nodes":[{"object":"text","text":"R. Comment and G. W. Schwert, “Poison or Placebo: Evidence on the Deterrence and Wealth Effects of Modern Antitakeover Measures,” ","marks":[]},{"object":"text","text":"Journal of Financial Economics","marks":[{"type":"italic"}]},{"object":"text","text":" 39 (1) (1995): 3–43; N. Varaiya, “Determinants of Premiums in Acquisition Transactions,” ","marks":[]},{"object":"text","text":"Managerial and Decision Economics","marks":[{"type":"italic"}]},{"object":"text","text":" 8 (3) (1987): 175–184; and R. Heron and E. Lie, “On the Use of Poison Pills and Defensive Payouts by Takeover Targets,” ","marks":[]},{"object":"text","text":"Journal of Business","marks":[{"type":"italic"}]},{"object":"text","text":" 79 (4) (2006): 1783–1807.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"12","marks":[]}],"object":"inline","type":"footnote","ref":"71d3a748-aad2-4103-a468-e474b0bb37e4"}],"data":{}}]}},"config":{"cssClass":""},"ref":"6ed3ef6c-a384-480f-8b0d-513886b574f5"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Poison Pills"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}