interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"114522a0-44e0-11ed-85e8-3aaa9aaf98ab","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"35200c22-8f34-488b-a1bc-bd29cdcb992d","nodes":[{"object":"text","text":"Golden Parachutes","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"4220f85d-e9fe-4955-a76d-8c843944b98d"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"12d1f265-fd1c-4e5b-b6cb-cd8b50bbcbce","nodes":[{"object":"text","text":"A ","marks":[]},{"data":{"ref":"9dfdf20a-11b4-49e5-8fc7-86dc73fe22bb","stage":{"ref":"a85a6167-fe37-4dee-ae4b-7be30f8d6800","sections":[{"ref":"6a096bda-cea5-421f-a501-28a48ab982a0","rows":[{"ref":"4fca102f-66e4-45fa-b06e-30f8eb77338b","columns":[{"ref":"d261b9f9-732a-4c65-ab11-fe0208a19dc6","stageElements":[{"ref":"76dea5c2-801f-430e-b6d0-a9b6dee9f8af","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"f17f21f0-c4a4-4f07-b7c6-57ee9011283e","nodes":[{"object":"block","type":"paragraph","ref":"a9cd7d0e-e1d5-4b91-9e36-475e454c4e3a","nodes":[{"object":"text","text":"An extremely lucrative severance package that is guaranteed to a firm’s senior managers in the event that the firm is taken over and the managers are let go.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"golden parachute","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"c2cf5f84-3505-44ce-99ad-4c0e348f602c"},{"object":"text","text":" is an extremely lucrative severance package that is guaranteed to a firm’s senior managers in the event that the firm is taken over and the managers are let go. For example, when Ronald Perelman successfully acquired Revlon Corporation, the firm’s former chairman, Michael Bergerac, was reported to have received a golden parachute compensation package worth in excess of $35 million.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"931cef32-8b87-4b2e-95dd-da3eb1d937f4"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"4b82100c-448f-462f-9406-9477d7e80f69","nodes":[{"object":"text","text":"Golden parachutes have been criticized because they are seen both as excessive and a misuse of shareholder wealth. In fact, the empirical evidence does not support this view.","marks":[]},{"data":{"ref":"96a01687-1944-45ac-8eca-18683901fe11","stage":{"ref":"fde10850-c426-41a7-a49e-e3b349dcf2ab","sections":[{"ref":"b470ad04-800b-4e88-bcdd-827fbc3dfebf","rows":[{"ref":"1c15c023-7a33-4c0e-82be-3b3e2c7f5256","columns":[{"ref":"8f72e3b0-d730-4f35-bf74-751972404e82","stageElements":[{"ref":"5f02e5cc-6943-4d86-b3f8-4f6d19ec1caa","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"2d1438e3-46ea-4c97-bbfe-47447538c604","nodes":[{"object":"block","type":"paragraph","ref":"c8e0f181-97b5-4117-ad41-700dc20d70d1","nodes":[{"object":"text","text":"M. Narayanan and A. Sundaram, “A Safe Landing? Golden Parachutes and Corporate Behavior,” ","marks":[]},{"object":"text","text":"University of Michigan Business School Working Paper No. 98015","marks":[{"type":"italic"}]},{"object":"text","text":" (1998).","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"13","marks":[]}],"object":"inline","type":"footnote","ref":"404e17c8-5b20-4c28-8c11-6ce79109f67b"},{"object":"text","text":" If anything, it supports the view that an adoption of a golden parachute actually creates value. If a golden parachute exists, management will be more likely to be receptive to a takeover. This means the existence of golden parachutes lessens the likelihood of managerial entrenchment. Researchers have found that stock prices rise on average when companies announce that they plan to implement a golden parachute policy, and that the number of firms bidding against one another for the target and the size of the takeover premium are higher if a golden parachute agreement exists.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"e62952bd-32ab-4dfb-bbab-b2c9c071d42e"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Golden Parachutes"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}