interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"12b1aaf0-44e0-11ed-8939-c9ad1c775027","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-one","ref":"afc97c6f-cab8-42e3-bae4-d5ab01b6d2ae","nodes":[{"object":"text","text":"23.5 Valuation and International Taxation","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"2f828ea2-55f2-44c5-a499-9c9c2e2de964"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"d000cff3-6956-4b82-9704-af040f42be22","nodes":[{"object":"text","text":"In this chapter, we assume that Ityesi pays a corporate tax rate of 25% no matter where its earnings are generated. In practice, determining the corporate tax rate on foreign income is complicated because corporate income taxes sometimes must be paid to two national governments: the host government (the United Kingdom in this example) and the home government (the United States). International taxation is a complex subject to which specialized experts devote considerable time. In this introductory setting, we aim only to provide a broad overview and sense of the issues involved.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"4eca2ef7-3e0b-44e6-81f8-cb6dc349dfa8"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"a03e68cb-d309-4a05-80e5-f33ae2d9f1a3","nodes":[{"object":"text","text":"The general international arrangement prevailing with respect to taxation of corporate profits is that the host country gets the first opportunity to tax income produced within its borders. The home government then gets an opportunity to tax the income from a foreign project to the domestic firm. In particular, the home government must establish a tax policy specifying its treatment of foreign income and foreign taxes paid on that income.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"c073a6e5-b577-4948-8ee3-89e7779b4e84"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"b459577e-0da4-4e68-80b7-42f0f8acd555","nodes":[{"object":"text","text":"Prior to the passage of the 2017 Tax Cuts and Jobs Act (TCJA), the United States taxed the worldwide earnings of U.S. companies at the same rate as profits earned in the United States, but with a full tax credit for any foreign taxes paid. Thus, if the host country had a tax rate of 10%, the U.S. firm would owe the difference between the U.S. tax rate of 35% and the 10% foreign tax rate. This additional 25% tax was deferred until the foreign profits were ","marks":[]},{"data":{"ref":"3a59f5bf-76b5-413c-9c53-54d1a6100ed1","stage":{"ref":"8dc11ec7-3542-4b22-8ebb-5390458673ce","sections":[{"ref":"8008d125-bdb0-4307-8c6a-978d8b8ef45b","rows":[{"ref":"16a7ed63-785e-46f7-8cdb-54cc99b97d79","columns":[{"ref":"f5b63fcc-d870-46ea-9998-be67fa883289","stageElements":[{"ref":"5ad634e1-8518-4b11-b3e9-5c0f4ab10576","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"949d3b35-dcf0-49ef-9088-9d325d58f315","nodes":[{"object":"block","type":"paragraph","ref":"21b66cae-55c5-4c52-9e2b-342f9846b419","nodes":[{"object":"text","text":"Refers to the profits from a foreign project that a firm brings back to its home country.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"repatriated","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"b58dfe2d-1cc0-449b-864d-216fb6b3cf60"},{"object":"text","text":" to the United States.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"52eee408-88ce-46a2-911d-a4cf20748ad9"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"f90faeea-59b8-4a40-a50d-c4345595c0e8","nodes":[{"object":"text","text":"There were two negative consequences of this system. First, it encouraged companies to keep their foreign profits overseas rather than repatriate them. In addition, because U.S. tax rates were among the highest in the world, it encouraged companies to move their most valuable assets, and even their legal headquarters, outside the United States.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"a748a204-ac36-4e10-ba70-6e80fabcea24"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"23.5 Valuation and International Taxation"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}