interactiveObj = {"$ambrosia":"aero:interactive:9417ac60-5f60-11ea-8e69-affb2224d7e8:*","$id":"e8db37f0-44df-11ed-8ca6-09c9e37626ec","$workspaceId":"7fff47c0-44d6-11ed-bcef-d5307f42495c","$projectId":"86cb0040-44df-11ed-8442-dbcbc27803ca","annotations":[],"configSchemaVersion":"/pluginGenericSchema/v1","config":{"stage":{"sections":[{"rowsAreFullWidth":false,"backgroundImageUrl":"","rows":[{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-two","ref":"4a941200-b793-4574-83a6-46e9e7d8fe6b","nodes":[{"object":"text","text":"Corporations","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"f3a0bf01-36d9-44bc-b37a-7dcfda31a89f"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"b9e35e6c-a1f2-4bee-be0f-6ff53e2eca36","nodes":[{"object":"text","text":"A ","marks":[]},{"data":{"ref":"a35a00b1-c63f-4ddf-b562-2b06cbd8a27d","stage":{"ref":"4e12aa6e-07ed-4715-8900-d9dcff338f2f","sections":[{"ref":"22501f5b-9844-41a8-982e-797c3b28d040","rows":[{"ref":"4b709fef-87d5-4e70-9659-a9fdeb29abd9","columns":[{"ref":"a16def2b-0d9b-4baa-b2b4-c7f177d57255","stageElements":[{"ref":"96741bc8-1a14-4549-8986-caa64f8f94a1","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"05d59081-f689-4eac-b06c-d2b5ac9387a3","nodes":[{"object":"block","type":"paragraph","ref":"3b3ff058-eeb9-4c31-a46c-cc49ac75ec7e","nodes":[{"object":"text","text":"A legally defined, artificial being, separate from its owners.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"corporation","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"45e52763-fcfe-4772-924a-06c40558040e"},{"object":"text","text":" is a legally defined, artificial being (a legal entity), separate from its owners. As such, it has many of the legal powers that people have. It can enter into contracts, acquire assets, and incur obligations, and it enjoys protection under the U.S. Constitution against the seizure of its property. Because a corporation is a legal entity separate and distinct from its owners, it is solely responsible for its own obligations. Consequently, the owners of a corporation (or its employees, customers, etc.) are not liable for any obligations the corporation enters into. Similarly, the corporation is not liable for any personal obligations of its owners.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"f2ca8b1c-b40f-4574-838f-484d7ccdc64c"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"9ef465ea-a371-459c-9aba-248688088901","nodes":[{"object":"text","text":"In the same way that it is difficult to imagine modern business life without e-mail and cell phones, the corporation revolutionized the economy. On February 2, 1819, the U.S. Supreme Court established the legal precedent that the property of a corporation, similar to that of a person, is private and entitled to protection under the U.S. Constitution.","marks":[]},{"data":{"ref":"4398d9de-3061-4fef-ac6d-94a7ae076a53","stage":{"ref":"0f6a7a2c-4092-426e-8acf-de723cb4399f","sections":[{"ref":"07c1235b-50a6-41fc-ad59-74480bcda4e0","rows":[{"ref":"5d8f5d5c-9035-47da-bf5a-472d30a3ac19","columns":[{"ref":"c0d27341-c81c-495d-bca4-10829e6e6643","stageElements":[{"ref":"b4982952-e61e-41df-b3dc-9493630d9964","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"eb23a5c8-1503-4906-b9ae-a4dd4c90a4c7","nodes":[{"object":"block","type":"paragraph","ref":"6093ca57-51b0-4363-9b48-bef8b17d680f","nodes":[{"object":"text","text":"The case was Dartmouth v. Woodward and the full text of John Marshall’s decision can be found at ","marks":[]},{"object":"inline","type":"link","ref":"f6af26cb-a153-4f56-b99a-1ad857c95aa0","nodes":[{"object":"text","text":"www.constitution.org/dwebster/dartmouth_decision.htm","marks":[]}],"data":{"href":"http://www.constitution.org/dwebster/dartmouth_decision.htm"}},{"object":"text","text":".","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"2","marks":[]}],"object":"inline","type":"footnote","ref":"3b2147db-7692-4fc4-b5f7-a372df85d76e"},{"object":"text","text":" This decision led to dramatic growth in the number of U.S. corporations from fewer than 1000 in 1830 to 50,000 in 1890. Today, the corporate structure is ubiquitous, not only in the United States (where it is responsible for 82% of business revenue), but all over the world.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"ababf8d3-b9d5-442a-8048-dabcd84af001"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"9a6ee221-49d5-4d8d-b84b-71780a9862ae","nodes":[{"object":"text","text":"Formation of a Corporation","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"6403211b-2f84-42d6-82f7-417da22678a6"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"a40361a2-d89c-428a-aaeb-3627600b7a7d","nodes":[{"object":"text","text":"A corporation must be legally formed, which means that the state in which it is incorporated must formally give its consent to the incorporation by chartering it. Setting up a corporation is therefore considerably more costly than setting up a sole proprietorship. The state of Delaware has a particularly attractive legal environment for corporations, so many corporations choose to incorporate there. For jurisdictional purposes, a corporation is a citizen of the state in which it is incorporated. Most firms hire lawyers to create a corporate charter that includes formal articles of incorporation and a set of bylaws. The corporate charter specifies the initial rules that govern how the corporation is run.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"3f3d4c7d-1fab-429f-b19b-a964d7fe4bd1"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"heading-three","ref":"5c7affa1-9222-4ea6-9ed5-aab0344fe7d5","nodes":[{"object":"text","text":"Ownership of a Corporation","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"f0eabcfe-cab8-41f3-b3cc-3a90a2a48262"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"87d516f7-5676-401c-840a-dc67429940a2","nodes":[{"object":"text","text":"There is no limit to the number of owners a corporation can have. Because most corporations have many owners, each owner owns only a fraction of the corporation. The entire ownership stake of a corporation is divided into shares known as ","marks":[]},{"data":{"ref":"103096ee-3e5f-4b0c-847c-45bd7665ed4b","stage":{"ref":"36886ce6-cd8f-4ea7-90cc-83c4f267c290","sections":[{"ref":"a7f0ca03-650e-4213-a0ae-3398a020763e","rows":[{"ref":"f99dbeb0-33d8-4664-b0de-b597a068538e","columns":[{"ref":"f159a8f0-91ec-431a-82fe-0b288e19f16e","stageElements":[{"ref":"18b002a0-86d1-41e7-bb35-34c19a4ff95c","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"d2211fa4-07cd-4524-951a-c12b07b62b29","nodes":[{"object":"block","type":"paragraph","ref":"1126feb7-78ba-44b6-8b3d-d228faf71b65","nodes":[{"object":"text","text":"The ownership or equity of a corporation divided into shares.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"stock","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"20a5ce88-3b04-4e60-bde9-c1ef6fd2d0d6"},{"object":"text","text":". The collection of all the outstanding shares of a corporation is known as the ","marks":[]},{"data":{"ref":"01f6baed-a15d-401b-97fd-65affe73c312","stage":{"ref":"912925a6-7a64-4594-85d0-9f8477c212a3","sections":[{"ref":"cd108abc-9c2b-43d2-9432-de8e9f7c76a9","rows":[{"ref":"90e78604-6d61-4ab1-866c-577ceab70c83","columns":[{"ref":"c4caae42-ad30-46bb-8567-8ca9099ae18c","stageElements":[{"ref":"79668c21-8e29-4580-90fc-c2415887b79c","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"22ce2505-04b7-48e1-b6e6-383ef8f11697","nodes":[{"object":"block","type":"paragraph","ref":"cbae8815-73fb-4920-8ff0-d78340cbd782","nodes":[{"object":"text","text":"The collection of all the outstanding shares of a corporation.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"equity","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"5d2e7e24-1148-4450-8072-d907d24f1b49"},{"object":"text","text":" of the corporation. An owner of a share of stock in the corporation is known as a ","marks":[]},{"data":{"ref":"93594773-323c-4c83-a9d1-3d1052d3c92d","stage":{"ref":"ef143c79-0f88-4a25-9c1e-3d8925571bd8","sections":[{"ref":"e2c3330d-3610-47d5-b50b-cf29e0ecbd13","rows":[{"ref":"f1f5d87a-e8a5-4fa9-814e-60f99363d8f5","columns":[{"ref":"255c3598-266c-4662-8a25-4eb2053c9cd0","stageElements":[{"ref":"c45e9a95-c472-4fa8-9820-be5b55029c49","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"675f01c9-3947-4458-b153-ae60a912af8e","nodes":[{"object":"block","type":"paragraph","ref":"6d506e11-4ddc-4889-b33b-827b7a090fa0","nodes":[{"object":"text","text":"An owner of a share of stock or equity in a corporation.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"shareholder, stockholder,","marks":[{"type":"bold"}]},{"object":"text","text":" or ","marks":[]},{"object":"text","text":"equity holder","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"67a5f45f-709b-45e0-a07d-4f6645896bb7"},{"object":"text","text":". Shareholders are entitled to ","marks":[]},{"data":{"ref":"07da0642-0e7a-476f-a5d3-29ef6e084a59","stage":{"ref":"bc918741-fc28-4adf-9008-57defa5b0a75","sections":[{"ref":"c7200728-f5d8-4c98-987b-e76ffc1c23c7","rows":[{"ref":"c28bbc1c-fc07-46b8-9cf6-f132d75b9416","columns":[{"ref":"dcee6750-bdd8-4834-812b-9067cbe6ad3d","stageElements":[{"ref":"0f698e82-2488-4547-a987-bf1e1925f7dc","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"8bc1284d-1204-49fb-a814-a21c8df354f9","nodes":[{"object":"block","type":"paragraph","ref":"92b0e8c4-312a-497b-a64b-baa15894cc3c","nodes":[{"object":"text","text":"Payments made at the discretion of the corporation to its equity holders.","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"dividend payments","marks":[{"type":"bold"}]}],"object":"inline","type":"key-term","ref":"f4f76e3f-4fd8-4a60-967b-7ff0b1a22452"},{"object":"text","text":"; that is, payments made at the discretion of the corporation to its equity holders. Shareholders usually receive a share of the dividend payments that is proportional to the amount of stock they own. For example, a shareholder who owns 25% of the firm’s shares would be entitled to 25% of the total dividend payment.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"13b5bb27-64b7-4a10-af1a-6096595f3289"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"ee840075-264d-4609-9813-4391873db630","nodes":[{"object":"text","text":"An important feature of a corporation is that there is no limitation on who can own its stock. That is, an owner of a corporation need not have any special expertise or qualification. This feature allows free and anonymous trade in the shares of the corporation and provides one of the most important advantages of organizing a firm as a corporation. Corporations can raise substantial amounts of capital because they can sell ownership shares to anonymous outside investors.","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"d34cc892-18f9-421d-bb84-cf16f2f5591f"}]}]},{"columns":[{"stageElements":[{"type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"paragraph","ref":"82d808c0-6ddd-4338-9205-302e799400dc","nodes":[{"object":"text","text":"The availability of outside funding has enabled corporations to dominate the economy. Let’s look at one of the world’s largest firms, Microsoft Corporation, as an example. Microsoft reported annual revenue of $110.4 billion over the 12 months from July 2017 through June 2018. The total value of the company (the owners’ collective wealth in the company) as of June 2018 was $768 billion. The company employed 131,000 people. Putting these numbers into perspective, treating the sales of $110.4 billion as gross domestic product (GDP) in 2017 would rank Microsoft (just ahead of Morocco) as the 62nd richest ","marks":[]},{"object":"text","text":"country","marks":[{"type":"italic"}]},{"object":"text","text":" (out of almost 200).","marks":[]},{"data":{"ref":"65ffb0ef-cf2f-47f5-b36a-0c5080ea2290","stage":{"ref":"c04669bc-76ee-4b1c-87e4-35232ad32f87","sections":[{"ref":"7cae4062-ea81-49ea-87db-879c03a75707","rows":[{"ref":"f28138b2-b4fe-4ccd-8d4d-64b9054424b9","columns":[{"ref":"2a0c55df-bb79-42bd-b042-9f1e7e89800c","stageElements":[{"ref":"5bceae47-85b5-4cdb-99bf-f2fdc7cc569a","type":"stage-element-text","value":{"object":"value","document":{"object":"document","data":{},"nodes":[{"object":"block","type":"button","ref":"ce3f32d0-3e4d-4344-b39c-d52b396d41a9","nodes":[{"object":"block","type":"paragraph","ref":"5a90d5e5-892d-403d-aa47-708baa5439c6","nodes":[{"object":"text","text":"World Development Indicators database, March 24, 2019. For quick reference tables on GDP, go to ","marks":[]},{"object":"inline","type":"link","ref":"eded0085-57c4-40c4-922d-0fff5806a356","nodes":[{"object":"text","text":"http://data.worldbank.org/indicator/NY.GDP.MKTP.CD","marks":[]}],"data":{"href":"http://data.worldbank.org/indicator/NY.GDP.MKTP.CD"}},{"object":"text","text":".","marks":[]}],"data":{}}],"data":{}}]}}}]}]}]}]}},"nodes":[{"object":"text","text":"3","marks":[]}],"object":"inline","type":"footnote","ref":"f73d40ee-9523-4f10-a401-409bc650a1d1"},{"object":"text","text":" Morocco has almost 35.8 million people, about 272 times as many people as employees at Microsoft. Indeed, if the number of Microsoft employees were used as the “population” of the corporation, Microsoft would rank just above Curacao as the 32nd least populous country on Earth!","marks":[]}],"data":{}}]}},"config":{"cssClass":""},"ref":"ce97e4f9-479a-4715-a099-19d165edd2c2"}]}]}]}]},"screenType":"","transitions":{"enter":{"animation":"","duration":0,"easing":"","customKeyframes":""},"exit":{"animation":"","duration":0,"easing":"","customKeyframes":""}},"lockElements":false,"learnerCurrentAttempt":1,"footer":{"buttonText":"Next","buttonIcon":"Arrow","showFooter":false,"showHistoryNavButtons":false,"previewFooter":false,"position":"Fixed to screen bottom","footerComponent":{"showFooterDropArea":"hide","width":100,"footerComponentsAlignment":"right"}},"styles":{"background":{},"stage":{"maxWidth":940,"background":"rgba(255, 255, 255, 1)","responsive":true,"padding":{"vertical":60,"horizontal":60},"border":{"color":"rgba(224, 224, 224, 1)","radius":0,"width":0}},"elements":{"padding":{"vertical":10,"horizontal":10}},"footer":{"buttonColor":"rgba(0, 115, 202, 1)","textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","padding":10,"corners":4,"border":"none","shadow":"0 -1px 4px 0 rgba(0,0,0,0.06), 0 0 12px 2px rgba(0,0,0,0.1)"},"feedback":{"width":320,"textColor":"rgba(0, 0, 0, 0.7)","backgroundColor":"rgba(255, 255, 255, 1)","corners":4,"border":"none","shadow":"0 5px 47px 10px rgba(0,0,0,0.15), 0 10px 20px 2px rgba(0,0,0,0.1), 0 7px 8px 0 rgba(0,0,0,0.1)"}},"title":"Corporations"},"assets":[],"scenarios":[],"pluginPayload":{"pluginRepositoryPath":"https://plugin-repository-bronte-qaint.pearson.com/9417ac60-5f60-11ea-8e69-affb2224d7e8/0.15.209/ac43ee4c0eda05e9718fa83a5aed4cff.zip","summary":{"pluginId":"9417ac60-5f60-11ea-8e69-affb2224d7e8","name":"Screen","description":"A screen of a lesson","type":"screen","latestVersion":"0.15.209","subscriptionId":"f0afd4d0-d2ff-11ea-9998-d57e1e28aca1","createdAt":"Fri, 6 Mar 2020 04:11:39 GMT","updatedAt":"Tue, 13 Dec 2022 22:07:46 GMT","tags":[],"publishMode":"DEFAULT"}},"activityThemeIconLibraries":[]}